mapper.select:
select b.*, (select count(id) from Comment where Comment.postid = b.id group by postId) commentTotal, (select nickname from Member where id = b.memId ) nickname, (select image from Member where id = b.memId ) memImage, (select name from BoardCategory where id = b.roleId) name, (select count(memId) from BoardLike where postId = b.id group by postId) hearts from Board b
roleId = #{roleId}
and useYN = "Y"
order by regDate desc;

select b.*, (select count(id) from Comment where Comment.postid = b.id group by postId) commentTotal, (select nickname from Member where id = b.memId ) nickname, (select name from BoardCategory where id = b.roleId) name, (select count(memId) from BoardLike where postId = b.id group by postId) hearts, (select image from Member where id = b.memId) memImage from Board b
roleId = #{roleId}
roleId != 5
and useYN = "Y"
order by regDate desc limit #{offset}, #{size}
