mapper.select:
select * from Exhibition
where corpId = #{id}
limit #{offset},#{size};

select * from Exhibition
where corpId = #{id}
limit #{limit};

mapper.update:
update Exhibition
name = #{name}, content = #{content}, startDate = #{startDate}, endDate = #{endDate}, startTime = #{startTime}, endTime = #{endTime}, ticketPrice = #{ticketPrice}, ticketStock = #{ticketStock}, artist = #{artist}, poster = #{poster},
where id = #{id}
